import Data from "./Data.json";
import SearchByName from "./SearchByName";
import { useState } from "react";

// Ref. https://www.youtube.com/watch?v=pQndRHoHd4Y
function Filters() {
  const [pets, setPets] = useState();

  const filteredResults = (filterType) => {
    const filteredPets = Data.filter((pet) =>
      pet.type === filterType);
    setPets(filteredPets);
  };

  console.log(Data);
  return (
    <div className="centerBox">
      <div className="buttons">
        <button id="filter-cats" onClick={() => filteredResults("cat")}>
          Filter Cats
        </button>
        {/* <button id="filter-dogs" onClick={filteredResults("dog")}>
          Filter Dogs
        </button>
