import Data from "./Data.json";
import SearchByName from "./SearchByName";
import { useState } from "react";

// Ref. https://codesandbox.io/p/sandbox/filter-with-react-button-r5x4i?file=%2Fsrc%2Findex.js
function Filters() {
  const [pets] = useState(Data);

  const filteredResults = (filterType) => {
    const filteredPets = pets.filter((pet) => pet.type === filterType);
    return filteredPets.map((pet, index) => (
      <li key={index}>{pet.name}</li>
    ));
  };



  console.log(Data);
  return (
    <div className="centerBox">
      <div className="buttons">
        <button id="filter-cats" onClick={filteredResults("cat")}>
          Filter Cats
        </button>
        <button id="filter-dogs" onClick={filteredResults("dog")}>
          Filter Dogs
        </button>
        <button id="filter-turtles" onClick={filteredResults("turtle")}>
          Filter Turtles
        </button>
        <button id="filter-rabbits" onClick={filteredResults("rabbits")}>
          Filter Rabbits
        </button>
        <button id="sort-by-name">
          <SearchByName />
        </button>
        <button id="reset" type="reset">
          Reset
        </button>
      </div>
    </div>
  );
}

export default Filters;
