import { useState } from "react";
import Data from "./Data.json";

// Ref. code from W7Class1 - https://codesandbox.io/p/sandbox/functioncomponent-d4w6l7?file=%2Fsrc%2FApp.js%3A15%[…]ebar%2522%253Atrue%252C%2522sidebarPanelSize%2522%253A15%257D
const FilteringFunction = () => {
  const [pets, setPets] = useState(Data);

  return (
    <>
      <h1>My Pets</h1>
      <input
        type="search"
        placeholder="Search Pet"
        onChange={(event) => {
          const filteredPets = pets.filter((pet) =>
            pet.name.toLocaleLowerCase().includes(event.target.value.toLocaleLowerCase())
          );
          setPets(filteredPets);
        }}
      />
      {pets.map((pet, index) => (
        <h2 key={index}>{pet.name}</h2>
      ))}
    </>
  );
};

export default FilteringFunction;
